import { Handle, Position } from "reactflow";
import { useState } from "react";
import { FiMic, FiX, FiGitBranch } from "react-icons/fi";

export const SendButton = ({ onClick, disabled }) => (
  <button
    onClick={onClick}
    disabled={disabled}
    className="px-3 py-2 rounded-lg bg-black text-white hover:bg-gray-800 disabled:opacity-40 transition"
  >
    Send
  </button>
);

const ChatNode = ({ id, data }) => {
  const [input, setInput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;
    data.onSend(id, input);
    setInput("");
  };

  return (
    <div className="group relative bg-white shadow-xl rounded-2xl w-[300px] border border-gray-200 overflo">

      <Handle type="target" position={Position.Top} />

      {/* Close */}
      <button
        onClick={() => data.onDelete(id)}
        className="absolute top-2 right-2 text-gray-400 hover:text-red-500 transition"
      >
        <FiX size={16} />
      </button>

      {/* Message */}
      <div className="p-4 text-sm text-gray-800 min-h-[80px] whitespace-pre-wrap">
        {data.label}
      </div>

      {/* Input */}
      <div className="w-full flex items-center gap-2 p-3 border-t bg-gray-300">
        <button className="text-gray-500 hover:text-black transition">
          <FiMic size={18} />
        </button>

        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
          placeholder="Type a message..."
          className="flex-1 text-sm px-3 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-black"
        />

        <SendButton onClick={handleSend} disabled={!input.trim()} />
      </div>

      {/* Branch Button */}
      <button
        onClick={() => data.onAddChild(id)}
        className="absolute -bottom-4 left-1/2 -translate-x-1/2 bg-white border border-gray-300 shadow-md rounded-full p-2 opacity-0 group-hover:opacity-100 transition hover:bg-black hover:text-white z-10"
      >
        <FiGitBranch size={16} />
      </button>

      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

export default ChatNode;
