import { Handle, Position } from "reactflow";
import { useState, useRef, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import {
  FiMic,
  FiX,
  FiGitBranch,
  FiChevronUp,
  FiChevronDown
} from "react-icons/fi";

export const SendButton = ({ onClick, disabled }) => (
  <button
    onClick={onClick}
    disabled={disabled}
    className="px-3 py-2 rounded-lg bg-black text-white text-xs hover:bg-gray-800 disabled:opacity-40 transition"
  >
    Send
  </button>
);

const ChatNode = ({ id, data }) => {
  const [input, setInput] = useState("");
  const [collapsed, setCollapsed] = useState(false);
  const [selectedApi, setSelectedApi] = useState("chatgpt");
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);

  const bottomRef = useRef(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, loading]);

  const handleSend = async () => {
    if (!input.trim() || loading) return;

    const userMessage = {
      role: "user",
      content: input
    };

    setMessages((prev) => [...prev, userMessage]);
    setInput("");
    setLoading(true);

    try {
      const res = await fetch("http://localhost:8000/api/ai", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          api: selectedApi,
          message: input
        })
      });

      const result = await res.json();

      const aiMessage = {
        role: "assistant",
        content: result.output || "No response"
      };

      setMessages((prev) => [...prev, aiMessage]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { role: "assistant", content: "⚠ Server error." }
      ]);
    }

    setLoading(false);
  };

  return (
    <div className="relative group bg-white shadow-xl rounded-xl w-[400px] border border-gray-200 flex flex-col">

      <Handle type="target" position={Position.Top} />

      {/* HEADER — Hidden until hover */}
      <div
        className={`
          flex items-center justify-between px-3 py-2 bg-gray-50
          transition-all duration-200
          opacity-0 -translate-y-2
          group-hover:opacity-100 group-hover:translate-y-0
          ${collapsed ? "rounded-xl" : "rounded-t-xl"}
        `}
      >
        <div className="flex items-center gap-2">
          <select
            value={selectedApi}
            onChange={(e) => setSelectedApi(e.target.value)}
            className="text-xs border border-gray-300 rounded px-2 py-1 bg-white outline-none cursor-pointer"
          >
            <option value="chatgpt">ChatGPT</option>
            <option value="gemini">Gemini</option>
          </select>

          <button
            onClick={() => setCollapsed(!collapsed)}
            className="text-gray-500 hover:text-black"
          >
            {collapsed ? <FiChevronDown size={16} /> : <FiChevronUp size={14} />}
          </button>
        </div>

        <button
          onClick={() => data.onDelete?.(id)}
          className="p-1 text-gray-400 hover:text-red-500"
        >
          <FiX size={16} />
        </button>
      </div>

      {!collapsed && (
        <>
          {/* CHAT AREA */}
          <div className="flex-1 overflow-y-auto p-4 space-y-3 min-h-[100px] max-h-[300px] bg-white cursor-default">

            {messages.map((msg, index) => (
              <div
                key={index}
                className={`flex ${
                  msg.role === "user" ? "justify-end" : "justify-start"
                }`}
              >
                <div
                  className={`max-w-[80%] px-3 py-2 rounded-2xl text-sm shadow-sm ${
                    msg.role === "user"
                      ? "bg-black text-white rounded-br-sm"
                      : "bg-gray-100 text-gray-800 rounded-bl-sm"
                  }`}
                >
                  <ReactMarkdown>{msg.content}</ReactMarkdown>
                </div>
              </div>
            ))}

            {loading && (
              <div className="flex justify-start">
                <div className="bg-gray-100 text-gray-500 px-3 py-2 rounded-2xl text-sm animate-pulse">
                  Thinking...
                </div>
              </div>
            )}

            <div ref={bottomRef} />
          </div>

          {/* INPUT — Visible on hover */}
          <div
            className="
              flex items-center gap-2 p-3 border-t bg-gray-100 rounded-b-xl
              opacity-0 translate-y-2
              group-hover:opacity-100 group-hover:translate-y-0
              transition-all duration-200 ease-in-out
            "
          >
            <button className="text-gray-500 hover:text-black">
              <FiMic size={16} />
            </button>

            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSend()}
              placeholder="Type a message..."
              className="flex-1 text-xs px-3 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-black"
            />

            <SendButton
              onClick={handleSend}
              disabled={!input.trim() || loading}
            />
          </div>
        </>
      )}

      {/* Bottom Hover Branch Button */}
      <div className="absolute bottom-0 left-0 w-full h-2 flex justify-center items-end">
        <div className="w-full h-full flex justify-center items-end">
          <button
            onClick={() => data.onAddChild?.(id)}
            className="mb-[-16px] bg-white border border-gray-300 shadow-md rounded-full p-2 opacity-0 group-hover:opacity-100 transition hover:bg-black hover:text-white z-10"
          >
            <FiGitBranch size={16} />
          </button>
        </div>
      </div>

      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

export default ChatNode;
