import { Handle, Position } from "reactflow";
import { useState } from "react";
import {
  FiMic,
  FiX,
  FiGitBranch,
  FiChevronUp,
  FiChevronDown
} from "react-icons/fi";

export const SendButton = ({ onClick, disabled }) => (
  <button
    onClick={onClick}
    disabled={disabled}
    className="px-3 py-1.5 rounded-lg bg-black text-white hover:bg-gray-800 disabled:opacity-40 transition"
  >
    Send
  </button>
);

const ChatNode = ({ id, data }) => {
  const [input, setInput] = useState("");
  const [collapsed, setCollapsed] = useState(false);
  const [selectedApi, setSelectedApi] = useState("chatgpt");

  const handleSend = () => {
    if (!input.trim()) return;

    data.onSend?.(id, {
      message: input,
      api: selectedApi
    });

    setInput("");
  };

  return (
    <div className="group relative bg-white shadow-lg rounded-2xl w-[320px] border border-gray-200 overflow-hidden">

      <Handle type="target" position={Position.Top} />

      {/* HEADER */}
      <div className="flex items-center justify-between px-3 py-2 border-b bg-gray-50">

        {/* Left Section: API + Collapse */}
        <div className="flex items-center gap-2">

          {/* API Dropdown */}
          <select
            value={selectedApi}
            onChange={(e) => setSelectedApi(e.target.value)}
            className="text-xs border border-gray-300 rounded px-2 py-1 bg-white focus:outline-none"
          >
            <option value="chatgpt">ChatGPT</option>
            <option value="gemini">Gemini</option>
            <option value="claude">Claude</option>
            <option value="mistral">Mistral</option>
          </select>

          {/* Collapse Button */}
          <button
            onClick={() => setCollapsed(!collapsed)}
            className="text-gray-500 hover:text-black transition"
          >
            {collapsed ? <FiChevronDown size={16} /> : <FiChevronUp size={16} />}
          </button>
        </div>

        {/* Close Button */}
        <button
          onClick={() => data.onDelete?.(id)}
          className="text-gray-400 hover:text-red-500 transition"
        >
          <FiX size={16} />
        </button>
      </div>

      {/* BODY */}
      {!collapsed && (
        <>
          {/* Message */}
          <div className="p-4 min-h-[120px] text-sm text-gray-800 whitespace-pre-wrap">
            {data.label}
          </div>

          {/* Input */}
          <div className="flex items-center gap-2 p-3 border-t bg-gray-100">
            <button className="text-gray-500 hover:text-black transition">
              <FiMic size={18} />
            </button>

            <input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleSend()}
              placeholder="Type a message..."
              className="flex-1 text-sm px-3 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-black"
            />

            <SendButton onClick={handleSend} disabled={!input.trim()} />
          </div>
        </>
      )}

      {/* Branch Button */}
      <button
        onClick={() => data.onAddChild?.(id)}
        className="absolute -bottom-4 left-1/2 -translate-x-1/2 bg-white border border-gray-300 shadow-md rounded-full p-2 opacity-0 group-hover:opacity-100 transition hover:bg-black hover:text-white z-10"
      >
        <FiGitBranch size={16} />
      </button>

      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

export default ChatNode;
