import React, { useCallback } from "react";
import ReactFlow, {
  Background,
  Controls,
  MiniMap,
  addEdge,
  useNodesState,
  useEdgesState
} from "reactflow";
import "reactflow/dist/style.css";
import ChatNode from "../components/ChatNode";
const { zoomIn, zoomOut, setViewport, getViewport } = useReactFlow();

const handleWheel = useCallback(
  (event) => {
    event.preventDefault();

    const { x, y, zoom } = getViewport();

    // CTRL + scroll → zoom
    if (event.ctrlKey) {
      if (event.deltaY < 0) {
        zoomIn({ duration: 100 });
      } else {
        zoomOut({ duration: 100 });
      }
      return;
    }

    // SHIFT + scroll → horizontal
    if (event.shiftKey) {
      setViewport({
        x: x - event.deltaY,
        y,
        zoom
      });
      return;
    }

    // Normal scroll → vertical
    setViewport({
      x,
      y: y - event.deltaY,
      zoom
    });
  },
  [getViewport, setViewport, zoomIn, zoomOut]
);

const nodeTypes = {
  chat: ChatNode
};

export default function Home() {
  const initialNodes = [
    {
      id: "1",
      type: "chat",
      position: { x: 400, y: 100 },
      data: {
        label: "Root message"
      }
    }
  ];

  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);

  const onConnect = useCallback(
    (params) =>
      setEdges((eds) => addEdge({ ...params, animated: true }, eds)),
    []
  );

  // Send message update
  const onSend = (id, message) => {
    setNodes((nds) =>
      nds.map((node) =>
        node.id === id
          ? { ...node, data: { ...node.data, label: message } }
          : node
      )
    );
  };

  // Add child node
  const addChild = (parentId) => {
    const newId = Date.now().toString();
    const parentNode = nodes.find((n) => n.id === parentId);

    const newNode = {
      id: newId,
      type: "chat",
      position: {
        x: parentNode.position.x,
        y: parentNode.position.y + parentNode.height + 60
      },
      data: {
        label: "New message"
      }
    };

    setNodes((nds) => [...nds, newNode]);

    setEdges((eds) => [
      ...eds,
      {
        id: `e${parentId}-${newId}`,
        source: parentId,
        target: newId,
        animated: true
      }
    ]);
  };

  // Delete node
  const deleteNode = (id) => {
    setNodes((nds) => nds.filter((n) => n.id !== id));
    setEdges((eds) =>
      eds.filter((e) => e.source !== id && e.target !== id)
    );
  };

  // Inject handlers into node data safely
  const nodesWithHandlers = nodes.map((n) => ({
    ...n,
    data: {
      ...n.data,
      onAddChild: addChild,
      onDelete: deleteNode,
      onSend
    }
  }));

  return (
    <div className="w-screen h-screen bg-neutral-900">
      <ReactFlow
        nodes={nodesWithHandlers}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        nodeTypes={nodeTypes}
        fitView

        zoomOnScroll={false}
  panOnScroll={false}
  panOnDrag={true}
      >
        <Background gap={20} size={1} color="#444" />
        {/* <MiniMap /> */}
        <Controls />
      </ReactFlow>
    </div>
  );
}
